Select e.company_code,e.founder,count(distinct(a.lead_manager_code)),count(distinct(b.senior_manager_code)), count(distinct(c.manager_code)),count(distinct(d.employee_code)) from Company e left join lead_manager a on e.company_code=a.company_code left join senior_manager b on e.company_code=b.company_code left join manager c on e.company_code=c.company_code left join employee d on e.company_code=d.company_code group by e.company_code,e.founder